# http://adventofcode.com/2016/day/2

numpad = [ "  1  ",
           " 234 ",
           "56789",
           " ABC ",
           "  D  "
         ]

instructions = [ "", "", "", "", "" ]

# You start at "5" and move up (to "2"), left (to "1"), and left (you can't, and stay on "1"), so the first button is 1.

x, y = (0, 2) # Coords of 5


code = []

def parse_move(char):
    
    global x
    global y


    new_x = x
    new_y = y

    if char == "U":
        new_y -= 1
    elif char == "D":
        new_y += 1
    elif char == "L":
        new_x -= 1
    elif char == "R":
        new_x += 1

    if new_x > 4:
        new_x = 4
    elif new_x < 0:
        new_x = 0

    if new_y > 4:
        new_y = 4
    elif new_y < 0:
        new_y = 0

    if numpad[new_y][new_x] == ' ':
        return
    else:
        x = new_x
        y = new_y

def record_digit():
    code.append(numpad[y][x])

if __name__ == "__main__":
    for row in instructions:
        for instruction in row:
            parse_move(instruction)

        record_digit()

    print(code)
    
